#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\programming\projects\Shortly\shortener\node_modules\mini-svg-data-uri\node_modules;C:\programming\projects\Shortly\shortener\node_modules;C:\programming\projects\Shortly\node_modules;C:\programming\projects\node_modules;C:\programming\node_modules;C:\node_modules;C:\programming\projects\Shortly\shortener\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/programming/projects/Shortly/shortener/node_modules/mini-svg-data-uri/node_modules:/mnt/c/programming/projects/Shortly/shortener/node_modules:/mnt/c/programming/projects/Shortly/node_modules:/mnt/c/programming/projects/node_modules:/mnt/c/programming/node_modules:/mnt/c/node_modules:/mnt/c/programming/projects/Shortly/shortener/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../mini-svg-data-uri/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../mini-svg-data-uri/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../mini-svg-data-uri/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../mini-svg-data-uri/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
